import React from 'react'
import styled from 'styled-components'
import './styles.css'

const TarefaList = styled.ul`
  list-style:none;
  padding: 0;
  width: 200px;
`

const Tarefa = styled.li`
  border:solid 1px black;
  height:10vh;
  padding:5px;
  text-align: left;
  text-decoration: ${({completa}) => (completa ? 'line-through' : 'none')};
`

const InputsContainer = styled.div`
  display: grid;
  grid-auto-flow: column;
  gap: 10px;
`

const Delete = styled.button `
  padding:3px;
`

class App extends React.Component {
    state = {
      tarefas: [],
      inputValue: '',
      filtro: ''

    }

  componentDidUpdate() {
    const listaLocal = this.state.tarefas
    localStorage.setItem("lista", JSON.stringify(listaLocal))
  };

  componentDidMount() {
    const listaString = localStorage.getItem("lista")
    const listaObjeto = JSON.parse(listaString)
    this.setState({tarefas: listaObjeto})
  };

  onChangeInput = (event) => {
    this.setState({inputValue: event.target.value})
  }

  criaTarefa = () => {
    console.log(this.state.inputValue)
    const tarefa = {
      texto: this.state.inputValue,
      completa : false,
      id : Math.random()
    }
    this.setState({tarefas: [...this.state.tarefas, tarefa]})
    this.setState({inputValue: ""})
  }

  selectTarefa = (id) => {
    const novaLista = this.state.tarefas
    const listaF = novaLista.map((tar) =>{
      if (tar.id === id){
        if (tar.completa === true){
        return ({
          texto: tar.texto,
          completa : false,
          id : tar.id
        })

      }
      else {
        return ({
          texto: tar.texto,
          completa : true,
          id : tar.id
      })
    }}
      else {
        return  ({
          texto: tar.texto,
          completa : tar.completa,
          id : tar.id
        })
      }
    })
    this.setState({tarefas: listaF})
  }

  onChangeFilter = (event) => {
    this.setState({filtro:event.target.value})
  }

  onClickDelete = (id) =>{
    console.log(id)
  }

  render() {
    const listaFiltrada = this.state.tarefas.filter(tarefa => {
      switch (this.state.filtro) {
        case 'pendentes':
          return !tarefa.completa
        case 'completas':
          return tarefa.completa
        default:
          return true
      }
    })

    return (
      <div className="App">
        <h1>Lista de tarefas</h1>
        <InputsContainer>
          <input value={this.state.inputValue} onChange={this.onChangeInput}/>
          <button onClick={this.criaTarefa}>Adicionar</button>
        </InputsContainer>
        <br/>

        <InputsContainer>
          <label>Filtro</label>
          <select value={this.state.filter} onChange={this.onChangeFilter}>
            <option value="">Nenhum</option>
            <option value="pendentes">Pendentes</option>
            <option value="completas">Completas</option>
          </select>
        </InputsContainer>
        <TarefaList>
          {listaFiltrada.map(tarefa => {
            return (<div>
              <Tarefa
                completa={tarefa.completa}
                onClick={() => this.selectTarefa(tarefa.id)}
              > 
                {tarefa.texto}
              </Tarefa>
              <Delete onClick={() => this.onClickDelete(tarefa.id)}>X</Delete>
              </div>
            )
          })}
        </TarefaList>
      </div>
    )
  }
}

export default App
